function Get-ADUserInfo {
    param (
        [string]$user,
        [string]$domain
    )

    # Define domain-specific credentials
    switch ($domain.ToLower()) {
        'aapico' {
            $domainController = 'aapico.com'
            $username = "aapico\ahroot"
            $password = ConvertTo-SecureString "A@pic0it@min" -AsPlainText -Force
        }
        'asico' {
            $domainController = 'asico.co.th'
            $username = "asico\it"
            $password = ConvertTo-SecureString "support" -AsPlainText -Force
        }
        default {
            Write-Output "Invalid domain"
            return
        }
    }

    # Create the PSCredential object
    $credential = New-Object -TypeName System.Management.Automation.PSCredential -ArgumentList $username, $password

    # Retrieve the user from the specified domain
    try {
        $user1 = Get-ADUser -Identity $user -Server $domainController -Credential $credential -Properties department, company | Select-Object department,company
        if ($user) {
            # Return the user information
            return [PSCustomObject]@{
                SamAccountName = $user1.SamAccountName
                Department     = $user1.Department
                Company        = $user1.Company
            }
        } else {
            Write-Output "User not found"
        }
    } catch {
        Write-Error "An error occurred: $_"
    }
}

function sendmail {
    param (
        [string]$texthtml,
        [string]$subject,
        [string]$sendto
    )
$smtpServer = "smtp.office365.com"
$smtpPort = 587
$smtpUser = "itsupport@aapico.com"
#$smtpPassword = "zyhkkcprwtyhtctv"
$smtpPassword ="support"
# สร้างออบเจ็กต์ SmtpClient
$smtp = New-Object Net.Mail.SmtpClient($smtpServer, $smtpPort)
$smtp.EnableSsl = $true
$smtp.Credentials = New-Object System.Net.NetworkCredential($smtpUser, $smtpPassword)
# สร้างออบเจ็กต์ MailMessage
$mailMessage = New-Object System.Net.Mail.MailMessage
$mailMessage.From = $smtpUser
#$itsupport = "itsupport@aapico.com"
#$mailMessage.To.Add("wachira.y@aapico.com")
#$mailMessage.To.Add("wajeepradit.p@aapico.com")
foreach($account in  $sendto)
{$mailMessage.To.Add($account)}

$mailMessage.Body = $texthtml
$mailMessage.IsBodyHtml = $true
$mailMessage.Subject = $subject
$smtp.Send($mailMessage)
}
function Invoke-MySqlQuery {
    param (
        [string]$Query
    )
    # MySQL connection details
    $DBUser = "wajeepradit.p"
    $DBPassword = ConvertTo-SecureString -String "Az_123456" -AsPlainText -Force
    $creds = New-Object -TypeName System.Management.Automation.PSCredential -ArgumentList $DBUser, $DBPassword
    $Server = "10.10.10.196"
    $Database = "glpi"
    $Port = 3306
    # Open MySQL connection
    $sqlConnect = Open-MySqlConnection -ConnectionName MyDBCon -Server $Server -Database $Database -Port $Port -Credential $creds -WarningAction SilentlyContinue
    # Execute the query
    $data = Invoke-SqlQuery -ConnectionName MyDBCon -Query $Query
    # Close the connection
    Close-SqlConnection -ConnectionName MyDBCon
    # Return the data
    return $data
}

function SoftwareHTML {
    param (
        [string]$sendto
    )

 $yesterday = (Get-Date).AddDays(-2)
# Format the date as yyyy-MM-dd
$date = $yesterday.ToString("yyyy-MM-dd")
$updatesoftware = Invoke-MySqlQuery -Query "SELECT * FROM glpi.glpi_softwares WHERE DATE(date_creation) = '$($date)';"
if ($updatesoftware -eq $null) {
    $result22 = emtymail
    sendmail -texthtml $result22 -subject "Automated Task: New Software Installation Recorded in GLPI" -sendto "itsupport@aapico.com"
    return 
}

$intsallcomputer = @()

foreach ($sf in $updatesoftware)
{
    Write-Output $sf.name
    $listversion = Invoke-MySqlQuery -Query "SELECT * FROM glpi.glpi_softwareversions WHERE softwares_id = '$($sf.id)';"
    $lv = $listversion.id -join ',' 
    $listcom = Invoke-MySqlQuery -Query "SELECT * FROM glpi.glpi_items_softwareversions WHERE softwareversions_id IN ($lv);"
    $cmid = $listcom.items_id -join ','
    $cpd = Invoke-MySqlQuery -Query @"
SELECT 
    glpi.glpi_computers.*,
    glpi.glpi_plugin_fusioninventory_inventorycomputercomputers.*,
    g1.name as 'groups',
    g2.name as 'groups in tech name',
    glpi.glpi_computertypes.name AS computertype_name,
    glpi.glpi_manufacturers.name AS manufacturer_name,
    glpi.glpi_states.name AS state_name,
    glpi.glpi_locations.completename AS location_name,
    glpi.glpi_autoupdatesystems.name  AS   asset_year
FROM 
    glpi.glpi_computers
INNER JOIN 
    glpi.glpi_computertypes ON glpi.glpi_computers.computertypes_id = glpi.glpi_computertypes.id
INNER JOIN 
    glpi.glpi_manufacturers ON glpi.glpi_computers.manufacturers_id = glpi.glpi_manufacturers.id
INNER JOIN 
    glpi.glpi_states ON glpi.glpi_computers.states_id = glpi.glpi_states.id
INNER JOIN 
    glpi.glpi_locations ON glpi.glpi_computers.locations_id = glpi.glpi_locations.id
INNER JOIN 
    glpi.glpi_groups AS g1 ON g1.id = glpi.glpi_computers.groups_id
INNER JOIN 
    glpi.glpi_groups AS g2 ON g2.id = glpi.glpi_computers.groups_id_tech
INNER JOIN 
    glpi.glpi_plugin_fusioninventory_inventorycomputercomputers ON glpi.glpi_plugin_fusioninventory_inventorycomputercomputers.computers_id = glpi.glpi_computers.id
INNER JOIN 
glpi.glpi_autoupdatesystems on glpi.glpi_autoupdatesystems.id =  glpi.glpi_computers.autoupdatesystems_id
    WHERE 
    glpi.glpi_computers.id IN ($cmid);
"@
     
    $softcom = [PSCustomObject]@{
        software = $sf
        listversion = $listversion
        listcom = $cpd
        itemversion = $listcom 
    }
    $intsallcomputer += $softcom
}

